  flex-wrap: wrap;
  place-content: center;
  place-items: center;
}
.count {
  font-size: 66px;
  font-weight: 700;
  font-family: cursive;
  margin-top: 70px;
  margin-bottom: 20px;
  max-width: 80vw;
  word-break: break-all;
  display: flex;
  place-content: center;
  place-items: center;
  transition: .1s;
}
.center {
  width: 225px;
  margin-top: 30px;
  margin-bottom: 30px;
}
.muyu {
  transition: .1s;
  width: 225px;
  height: 168px;
}
.flex {
  display: flex;
  flex-wrap: wrap;
  margin: 20px;
}
@keyframes fade {
  0% {
    opacity: 1;
    margin-top: -130px;
  }
  100% {
    opacity: 0;
    margin-top: -180px;
  }
}
p {
  position: absolute;
  opacity: 0;
  animation: fade ease-out .6s;
}
button {
  font-size: 12px;
  font-weight: 700;
  color: #4360b7;
  background-color: #e7eeff;
  padding: .65em .9em;
  margin: 5px;
  border-radius: 2em;
  border: none;
  transition: .3s;
}
button:active { background-color: #d6ddee; }
span {
  font-size: 13px;
  color: #aaa;
}
</style>
</head>
<body>
<div class="count">0</div>
<span>本次累积功德</span>
<div class="center">
  <img class="muyu" src="{{$.img}}" onclick="Knock();">
</div>
<div class="flex">
  <button onclick="Bgm();">开启沉浸式BGM(约27.3MB)</button>
  <button onclick="Auto();">自动敲</button>
</div>
<audio id="bgm" src="http://f3.htcyy.com/play9/973/mp3/2" preload="none" loop hidden></audio>
<span>敲电子木鱼，见机甲佛祖，颂赛博真经。</span>
</body>
<script>
function $(t) {
  return document.querySelector(t);
}
function Count() {
  c = $('.count');
  m = $('.muyu');
  c.textContent = ++c.textContent;
  c.style.transform = "scale(1.15)";
  m.style.transform = "scale(.95)";
  setTimeout (function () {
    c.style.transform = "scale(1)";
    m.style.transform = "scale(1)";
  }, 100);
  t = document.createElement('p');
  t.textContent = "功德 + 1";
  $('.center').appendChild(t);
  setTimeout (function () {
    $('p').remove();
  }, 600);
}
function Knock() {
  x = document.createElement('audio');
  x.src = "data:audio/aac;base64,";
  x.onplaying = Count;
  x.onended = x.remove;
  x.play();
}
function Bgm() {
  x = $('#bgm');
  s = $('[onclick="Bgm();"]');
  x.volume = .2;
  if (x.paused) {
    x.play();
    s.textContent = "暂停沉浸式BGM";
  } else {
    x.pause();
    s.textContent = "继续沉浸式BGM";
  }
}
function Auto() {
