@js:id=baseUrl.match(/(\d+)/)[1];
var b="https://m.weibo.cn/detail/"+id;
var html=String(java.ajax(b));

if(html.indexOf('"media_info"')>-1){
urls=JSON.parse(html.match(/"urls": ({[\s\S]+?})/)[1]);
media_info=JSON.parse(html.match(/"media_info": ({[\s\S]+?})/)[1]);

if(urls.mp4_720p_mp4){a=urls.mp4_720p_mp4}else if(urls.mp4_hd_mp4){a=urls.mp4_hd_mp4}else if(media_info.stream_url_hd){a=media_info.stream_url_hd}else{a=media_info.stream_url}

result=result.replace(/data-url="([^"]+)"\s*href="([^"]+)"/,"data-url=\"$1\" href=\""+a+"\"");
dimg=html.match(/"page_pic":[\s\S]+?"url":\s*"([^"]+)"/)[1];

result=result+"<video src=\""+a+"\" width=100% height=auto controls=\"controls\"  poster=\""+dimg+"\"></video>";
}else if(html.match(/"size":\s*"large",\s*"url":\s*"[^"]+"/g)){
a=html.match(/"size":\s*"[^"]+",\s*"url":\s*"[^"]+"/g);
    var cimg=[];
    for (i=0;i<a.length;i++){
       cimg.push(a[i].match(/url":\s*"([^"]+)"/)[1]);
    };
    var bimg="";
    for (i=0;i<cimg.length;i++){
        bimg=bimg+"<p></p><img src="+cimg[i]+"><img>"
    }
result=result+bimg; 
}else{result=result}


if(JSON.parse(java.ajax("https://m.weibo.cn/comments/hotflow?id="+id+"&mid="+id+"&max_id_type=0")).ok==1){
list=JSON.parse(java.ajax("https://m.weibo.cn/comments/hotflow?id="+id+"&mid="+id+"&max_id_type=0")).data.data;
result="<a href="+b+">原文地址</a>"+"<p></p>"+result;
    for(i=0;i<list.length;i++){
    details="";
    if(list[i].pic!=undefined){
    pic=list[i].pic.large.url;
    details="<details><summary>查看图片</summary><p><img src=\""+pic+"\" alt=\"查看图片\"></p></details>"
}
result=result+"<p></p><br>"+"<img src="+list[i].user.profile_image_url+"width=20px height=20px></img>"+"<strong>"+list[i].user.screen_name+"</strong>"+"<br>"+list[i].text+""+details+"<p style=\"color:\#\#FFC4C4C4\;font-size:10px\">"+list[i].created_at.replace(/\+0800/,"·")+"</p>"+"<p></p>"
      }}else{result="<a href="+b+">原文地址</a>"+"<p></p>"+result};

result="<html><head><meta charset=\"utf-8\"><title>微博</title><style>img\{max-width: 100%\;max-height: 100%\;\}summary\{color:\#\#FF2D7A7E\;\}</style></head>"+ "<body>"+result+"</body></html>"

