script='<script type="text/javascript">\n(function(){$("#bon0").addClass("upBtn");var dplayer = new DPlayer({\nelement:document.getElementById("dplayer"),\nautoplay: true,\nlang: \'zh-cn\',\nvideo: {\nurl: bon0.value,\npic: ""\n}\n});})();\nfunction chooseType(e) {$("button").removeClass("upBtn");$("#"+e).addClass("upBtn");}function vv(url){var dplayer = new DPlayer({\nelement:document.getElementById("dplayer"),\nautoplay: true,\nlang: \'zh-cn\',\nvideo: {\nurl: url,\npic: ""\n}\n});}\nfunction change(e){$("li").removeClass("active");$("#"+e).addClass("active");if(e=="b1"){m3u8.style.display="block";mp4.style.display="none";des.style.display="none"}else if(e=="b2"){mp4.style.display="block";m3u8.style.display="none";des.style.display="none"}else if(e=="b3"){mp4.style.display="none";m3u8.style.display="none";des.style.display="block"}};$("#dxb").click(function(){if($("#dxb").text()=="倒序"){$("#dxb").text("正序");$("#dx3,#dx4").show();$("#zx3,#zx4").hide();}else{$("#dxb").text("倒序");$("#dx3,#dx4").hide();$("#zx3,#zx4").show();}})</script>';

play1=java.getElements('@@id.play_1@ul@li').toArray();
zx3='';dx3='';zx4='';dx4='';
for(i in play1){
title=play1[i].text().match(/(.*?)\$/)[1];
title1=play1[play1.length-1-i].text().match(/(.*?)\$/)[1];
src=play1[i].text().match(/\$(.*)/)[1];
src1=play1[play1.length-1-i].text().match(/\$(.*)/)[1];
zx3=zx3+'<button id="bon'+i+'" class="button" onclick="chooseType(this.id);vv(this.value)" value="'+src+'">'+title+'</button>';
dx3=dx3+'<button id="bonn'+i+'" class="button" onclick="chooseType(this.id);vv(this.value)" value="'+src1+'">'+title1+'</button>';
}
zx3='<div id="zx3" style="display:block">'+zx3+'</div>';
dx3='<div id="dx3" style="display:none">'+dx3+'</div>';


play2=java.getElements('@@id.down_1@ul@li').toArray();
for(i in play2){
title=play2[i].text().match(/(.*?)\$/)[1];
title1=play2[play2.length-1-i].text().match(/(.*?)\$/)[1];
src=play2[i].text().match(/\$(.*)/)[1];
src1=play2[play2.length-1-i].text().match(/\$(.*)/)[1];
zx4=zx4+'<button id="bont'+i+'" class="button" onclick="chooseType(this.id);vv(this.value)" value="'+src+'">'+title+'</button>';
dx4=dx4+'<button id="bontt'+i+'" class="button" onclick="chooseType(this.id);vv(this.value)" value="'+src1+'">'+title1+'</button>';
}
zx4='<div id="zx4" style="display:block">'+zx4+'</div>';
dx4='<div id="dx4" style="display:none">'+dx4+'</div>';

tap='<div id="tab"><ul><li id="b1" class="active" onclick="change(this.id)">m3u8</li> <li id="b2" onclick="change(this.id)">mp4</li><li id="b3" onclick="change(this.id)">剧情介绍</li><button id="dxb">倒序</button></ul> </div>';

m3u8='<div id="m3u8" style="display:block">'+zx3+dx3+'</div>';
mp4='<div id="mp4" style="display:none">'+zx4+dx4+'</div>';
des='<div id="des" style="display:none">'+java.getString("@@class.vodImg@html",false)+java.getString("@@class.more@html",false)+'</div>';

result='<html>'+html+'<hr>'+tap+m3u8+mp4+des+script+'</body></html>'

