@js:
var title = java.getElements(".entry-header");

var content = java.getElements(".single-content");

var doc = org.jsoup.Jsoup.parse(content);

//解析剩下页数,并追加剩余页数内容

var pageNum = null;
var other = [];
if("{{@@page-links}}"!=null){
var page = doc.select(".page-links").select("a");
pageNum = page.size()-1;
for(var i = 1;i < pageNum;i++){
var doc = org.jsoup.Jsoup.connect(page.get(i).attr("href")).get()
other[i] = doc.select(".single-content");
}
}

var all = title +'<hr><p>'+content+other.join(" ")+'<hr><p><a href='+baseUrl+'>原文出处</a>'

result = String(all).replace(/(浏览 次)/,"")

