@js:
var title = java.getElements(".entry-title");

var content = java.getElements(".entry-content");

var date = java.getElements(".wpd-comment-date");

var  text = java.getElements(".wpd-comment-text");

var author = java.getElements(".wpd-comment-author")
var list = [];
for(var i = 0;i < author.size();i++){
list[i] ='<p><a id = "author">'+author.get(i).text()+'</a><br><br><a id = "text">'+text.get(i).text()+'</a><br><br><a id = "date">'+date.get(i).text()+'</a><hr>'
}
var clean = (content+list.join(" ")).replace(/(\<button.*\】)|(\<pre\>\<a.*pre\>)/g,"");

var convert = clean.replace(/\<pre\>/g, "\<a id=\"convert\"\>").replace(/\<\/pre\>/g,"\<\/a\>")

result = title+convert

