var d = new Date(str);
var weeks = d.getFullYear() + '/' + (d.getMonth() + 1) + '/' + d.getDate() + '  ' + d.getHours() + ':' + d.getMinutes() + ':' + d.getSeconds();

return weeks
}
var time = "{{@@class.entry-date@datetime}}";

var author = "{{@@class.author@tag.a@text}}"
result = myFormat(time)+'           '+author

