zxb='';dxb='';
list=doc.select('a:not(:last-child)').toArray();
for(i in list){
a='http://www.yhdm.io'+list[i].select('a').attr('href');
b='http://www.yhdm.io'+list[list.length-1-i].select('a').attr('href');
zxb=zxb+'<button id="but'+i+'" class="button" value="'+a+'">'+list[i].text()+'</button>';
dxb=dxb+'<button id="dbut'+i+'" class="button" value="'+b+'">'+list[list.length-1-i].text()+'</button>'
}
zx='<div id="zx" style="display:block">'+zxb+'</div>';
dx='<div id="dx" style="display:none">'+dxb+'</div>';


script='(function(){$(".button:first").addClass("upBtn");$("#ff").attr("src",$(".button:first").val())})();$(".button").click(function(){id=$(this).attr("id");\nid="#"+id;\n$(".button").removeClass("upBtn");\n$(id).addClass("upBtn");\nurl=$(id).val();$("#ff").attr("src",url)});$("li").click(function(){id=$(this).attr("id");\nid="#"+id;\n$("li").removeClass("active");\n$(id).addClass("active");\nif(id=="#b1"){$("#juji").show();$("#info").hide()}else{$("#info").show();$("#juji").hide()}});$("#dxb").click(function(){if($("#dxb").text()=="倒序"){$("#dxb").text("正序");$("#dx").show();$("#zx").hide();}else{$("#dxb").text("倒序");$("#dx").hide();$("#zx").show();}}) ';

tap='<div id="tab" style="display:inline;"><ul><li id="b1" class="active">剧集</li><li id="b2">剧情介绍</li><button id="dxb" style="display:inline">倒序</button></ul></div>';


juji='<div id="juji" style="display:block">'+zx+dx+'</div>';
info='<div id="info" style="display:none">'+img+info+'</div>';

