.entry-content@tag.img@data-original##\n##\$
<js>result</js>

<meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0">
<link rel="stylesheet" href="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.css">
<script src="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.js"></script>
<script src="http://libs.baidu.com/jquery/2.0.0/jquery.min.js"></script>
<h3>{{@@.entry-title@text}}</h3>
<h6>{{@@.entry-date@text}}</h6>
<div id="box"></div>
<h6 id="loads"><i>加载中...</i></h6>

<script>
    srclist = "{{result}}".split("$")
    var page = srclist.length;
    var time = 2;

    var box = document.getElementById("box");
    var loads = document.getElementById("loads");


    var lock = new sampleLock();
    lock.lock();
    var h5 = '<h5 style="border-left:2px solid#000;margin-bottom:0;padding-left:5;">';
    var h55 = '/' + page + '</h5>';


    $(document).ready(function () {
        new Viewer(box, { title: false, interval: 3000 });
        //初始加载四张图
        lock.unlock();
        for (var j = 0; j < time; j++) {
            getImg(page, srclist)
        }
        io = new IntersectionObserver((x) => {
            if (x[0].intersectionRatio <= 0) return;

            main();
        }).observe(loads);

    })

    function main() {
        console.log("start");
        for (I = 0; I < time; I++) {
            getImg(page, srclist);
        }
        console.log("finish");
    }

    async function getImg(page, srclist) {
        await lock.status;
        lock.lock();
        var index = lock.index;
        if (index <= page && srclist[index] != undefined) {
            
            var img = new Image();
            img.src = srclist[index];

            img.onload = function () {
                callback(img.width, img.height);
                $('#box').append(h5 + (index + 1) + h55);
                $('#box').append(img);
                box.viewer.update();
            }

            if (img.complete) {
                callback(img.width, img.height);
                lock.unlock();
                return;
            }

        } else {
            $('#loads').html("");
        }
        
    }


    function sampleLock() {
        this.index = -2;
        this.callback = null
        this.status = new Promise((resolve) => { resolve(true) })
        this.lock = function () {
            this.status = new Promise((resolve) => { callback = resolve })
            console.log("locked")
            this.index++;
        }
        this.unlock = function () { console.log("unlocked  "+this.index);callback(true); }
    }
</script>

