</script>
<script>
    function pp(a) {
        b = a.replace(/\\/, '');
        $("#textReplace").value += b;
        $("#textReplace").focus()
    }

    function dis() {
        document.getElementById("bt1").disabled = true;
        document.getElementById("bt2").disabled = true
    }

    function di() {
        document.getElementById("bt1").disabled = false;
        document.getElementById("bt2").disabled = false
    }

    function parseToDOM(str) {
        var div = document.createElement("div");
        if (typeof str == "string") div.innerHTML = str;
        return div.querySelectorAll("a");;
    }

    function okpage() {
        pageclass = $("#pageclass").value.replace(/(\+|\?)/g, '\\$1');
        pageclass = pageclass.replace(/分类$/g, '(.*)').replace(/分类(?!$)/g, '(.*?)');
        reg = new RegExp(pageclass)
        pageclass2 = $("#pageclass2").value;
        html = $("#input").value;
        pageclass2 = pageclass2.replace(/页码/g, '{\{page}}');
        pageclass3 = $("#pageclass3").value.replace(/(\+|\?)/g, '\\$1').replace(/页码/, '{\{page}}')
        h = "";
        if (html.match(/::/) && pageclass != "" && pageclass2 != "" && !/{\{page}}/.test(html)) {
            list = html.split(/\n|&&/);
            for (i in list) {
                if (list[i].match(/::/)) {
                    text = list[i].match(/(.*?)::/)[1];
                    href = list[i].match(/::(.*)/)[1]
                    if (href.match(reg)) {
                        href = pageclass2.replace(/分类/g, href.match(reg)[1])
                    } else {
                        href = href
                    }
                    h += text + "::" + href + "\n"
                }
            }
            $("#input").value = h
        }
        html = $("#input").value
        if (pageclass3 != "" && !/<,.*?>/.test(html)) {
            reg = new RegExp("(" + pageclass3 + ")", 'g');
            $("#input").value = html.replace(reg, '<,$1>')
        }
    }

    function getInput() {
        var str = $("#input").value;
        var list = parseToDOM(str);
        if (list.length == 0) {
            $("#input").placeholder = "格式化失败！！！请检查是否为带html标签的文本"
        } else {
            var html = "";
            for (let i = 0; i < list.length; i++) {
                let text = list[i].innerText;
                let href = list[i].getAttribute("href");
                html += text + "::" + href + "\n"
            }
            $("#pageclass").value = html.match(/.*?::(.*?)\n/)[1];
            $("#input").value = html
        }
    }

    function getInput2() {
        var str = $("#input").value;
        var list = parseToDOM(str);
        if (list.length == 0) {
            $("#input").placeholder = "格式化失败！！！请检查是否为带html标签的文本"
        } else {
            var html = [];
            for (let i = 0; i < list.length; i++) {
                let text = list[i].innerText;
                let href = list[i].getAttribute("href");
                html.push({
                    title: text,
                    url: href
                })
            }
            $("#input").value = JSON.stringify(html)
        }
    }

    function getInput3() {
        text = $("#input").value;
        if (text.match(/\&\&|::/g)) {
            text = text.replace(/\&\&/g, '\n');
            list = text.match(/.*::.*/g);
            html = [];
            for (i = 0; i < list.length; i++) {
                l = list[i].match(/(.*)::(.*)/);
                title = l[1];
                url = l[2]
                html.push({
                    title: title,
                    url: url
                })
            }
            $("#input").value = JSON.stringify(html)
        } else {
            html = "";
            json = eval(text);
            for (i in json) {
                html += json[i].title + "::" + json[i].url + "\n"
            }
            $("#input").value = html
        }
    }

    function getInput4(flag, x) {
        text = $("#input").value;
        if (flag == 1) {
            json = JSON.parse(text);
            for (i in json) {
                json[i].style = {}
                json[i].style.layout_flexGrow = 1
            }
        } else if (flag == 2) {
            data = {
                1: 1,
                2: 0.4,
                3: 0.25,
                4: 0.2
            }
            json = JSON.parse(text);
            for (i in json) {
                json[i].style.layout_flexBasisPercent = data[x]
            }
        }
        $("#input").value = JSON.stringify(json)
    }

    function setVisible(idElement, visible) {
        var obj = document.getElementById(idElement);
        obj.style.visibility = visible ? "visible" : "hidden";
    }

    function isValidFields() {
        var textSour = document.getElementById("textSour");
        if (null == textSour.value || textSour.value.length < 1) {
            textSour.focus();
            JsonsMessageBox($("#textSour"), "请输入待匹配文本");
            return false;
        }
        var textPattern = document.getElementById("textPattern");
        if (null == textPattern.value || textPattern.value.length < 1) {
            textPattern.focus();
            JsonsMessageBox($("#textPattern"), "请输入正则表达式");
            return false;
        }
        return true;
    }

    function buildRegex() {
        var op = "";
        if (document.getElementById("optionGlobal").checked) op = "g";
        if (document.getElementById("optionIgnoreCase").checked) op = op + "i";
        return new RegExp(document.getElementById("textPattern").value, op);
    }

    function onMatch() {
        if (!isValidFields()) return false;
        document.getElementById("textMatchResult").value = "";
        var regex = buildRegex();
        var result = document.getElementById("textSour").value.match(regex);
        if (null == result || 0 == result.length) {
            document.getElementById("textMatchResult").value = "（没有匹配）";
            return false;
        }
        if (document.getElementById("optionGlobal").checked) {
            var re = "";
            var strResult = "共找到 " + result.length + " 处匹配：\r\n";
            document.getElementById("textResult").innerText = strResult;
            for (var i = 0; i < result.length; ++i) {
                re += result[i] + "\r\n";
            }
            document.getElementById("textMatchResult").value = re;
        } else {
            document.getElementById("textMatchResult").value = "匹配位置：" + regex.lastIndex + "\r\n匹配结果：" + result[0];
        }
        return true;
    }

    function onReplace() {
        var str = document.getElementById("textSour").value;
        var regex = buildRegex();
        document.getElementById("textReplaceResult").value = str.replace(regex, document.getElementById("textReplace").value);
    }

    function reset() {
        $("#textSour").value = "";
        $("#textPattern").value = "";
        $("#textMatchResult").value = "";
        $("#textReplace").value = "";
        $("#textReplaceResult").value = "";
    }
    $("#regCommon a").click(function() {
        $("#textPattern").val($(this).attr("t"));
        onMatch();
    });

    function Empty() {
        $("#textSour").value = "";
        $("#textMatchResult").value = "";
        $("#textReplace").value = "";
        $("#textReplaceResult").value = "";
        $("#textSour").select();
    }


// 格式方法
// 公共方法
function transitionJsonToString (jsonObj, callback) {
	// 转换后的jsonObj受体对象
	var _jsonObj = null;
	// 判断传入的jsonObj对象是不是字符串，如果是字符串需要先转换为对象，再转换为字符串，这样做是为了保证转换后的字符串为双引号
	if (Object.prototype.toString.call(jsonObj) !== "[object String]") {
		try {
			_jsonObj = JSON.stringify(jsonObj);
		} catch (error) {
			// 转换失败错误信息
			alert('您传递的json数据格式有误，请核对...');
			alert(error);
			callback(error);
		}
	} else {
		try {
			jsonObj = jsonObj.replace(/(\')/g, '\"');
			_jsonObj = JSON.stringify(JSON.parse(jsonObj));
		} catch (error) {
			// 转换失败错误信息
			alert('您传递的json数据格式有误，请核对...');
			alert(error);
			callback(error);
		}
	}
	return _jsonObj;
}
// callback为数据格式化错误的时候处理函数
function formatJson (callback) {
    var jsonObj = $("#input").value;
	// 正则表达式匹配规则变量
	var reg = null;
	// 转换后的字符串变量
	var formatted = '';
	// 换行缩进位数
	var pad = 0;
	// 一个tab对应空格位数
	var PADDING = '  ';
	// json对象转换为字符串变量
	var jsonString = transitionJsonToString(jsonObj, callback);
	if (!jsonString) {
		return jsonString;
	}
	// 存储需要特殊处理的字符串段
	var _index = [];
	// 存储需要特殊处理的“再数组中的开始位置变量索引
	var _indexStart = null;
	// 存储需要特殊处理的“再数组中的结束位置变量索引
	var _indexEnd = null;
	// 将jsonString字符串内容通过\r\n符分割成数组
	var jsonArray = [];
    // 正则匹配到{,}符号则在两边添加回车换行
	jsonString = jsonString.replace(/([\{\}])/g, '\r\n$1\r\n');
	// 正则匹配到[,]符号则在两边添加回车换行
	jsonString = jsonString.replace(/([\[\]])/g, '\r\n$1\r\n');
	// 正则匹配到,符号则在两边添加回车换行
	jsonString = jsonString.replace(/(\,)/g, '$1\r\n');
	// 正则匹配到要超过一行的换行需要改为一行
	jsonString = jsonString.replace(/(\r\n\r\n)/g, '\r\n');
	// 正则匹配到单独处于一行的,符号时需要去掉换行，将,置于同行
	jsonString = jsonString.replace(/\r\n\,/g, ',');
	// 特殊处理双引号中的内容
	jsonArray = jsonString.split('\r\n');
	jsonArray.forEach(function (node, index) {
		// 获取当前字符串段中"的数量
		var num = node.match(/\"/g) ? node.match(/\"/g).length : 0;
		// 判断num是否为奇数来确定是否需要特殊处理
		if (num % 2 && !_indexStart) {
			_indexStart = index
		}
		if (num % 2 && _indexStart && _indexStart != index) {
			_indexEnd = index
		}
		// 将需要特殊处理的字符串段的其实位置和结束位置信息存入，并对应重置开始时和结束变量
		if (_indexStart && _indexEnd) {
			_index.push({
				start: _indexStart,
				end: _indexEnd
			})
			_indexStart = null
			_indexEnd = null
		}
	})
	// 开始处理双引号中的内容，将多余的"去除
	_index.reverse().forEach(function (item, index) {
		var newArray = jsonArray.slice(item.start, item.end + 1)
		jsonArray.splice(item.start, item.end + 1 - item.start, newArray.join(''))
	})
	// 奖处理后的数组通过\r\n连接符重组为字符串
	jsonString = jsonArray.join('\r\n');
	// 将匹配到:后为回车换行加大括号替换为冒号加大括号
	jsonString = jsonString.replace(/\:\r\n\{/g, ':{');
	// 将匹配到:后为回车换行加中括号替换为冒号加中括号
	jsonString = jsonString.replace(/\:\r\n\[/g, ':[');
	// 将上述转换后的字符串再次以\r\n分割成数组
	jsonArray = jsonString.split('\r\n');
    // 将转换完成的字符串根据PADDING值来组合成最终的形态
    jsonArray.forEach(function (item, index) {
		console.log(item)
    	var i = 0;
    	// 表示缩进的位数，以tab作为计数单位
		var indent = 0;
		// 表示缩进的位数，以空格作为计数单位
		var padding = '';
		if (item.match(/\{$/) || item.match(/\[$/)) {
			// 匹配到以{和[结尾的时候indent加1
			indent += 1
		} else if (item.match(/\}$/) || item.match(/\]$/) || item.match(/\},$/) || item.match(/\],$/)) {
			// 匹配到以}和]结尾的时候indent减1
			if (pad !== 0) {
				pad -= 1
			}
        } else {
            indent = 0
        }
        for (i = 0; i < pad; i++) {
            padding += PADDING
        }
        formatted += padding + item + '\r\n'
		pad += indent
   	})
	// 返回的数据需要去除两边的空格
	$("#input").value = formatted.trim();

}


</script>
