</div>



<br>
<div>
<p>资源导入格式</p>
<div class="path">
		<button onclick="xuan(this,1)" value="auto"><b>自动识别</b></button>
		<button onclick="xuan(this,1)" value="bookSource"><b>书源导入</b></button>
		<button onclick="xuan(this,1)" value="rssSource"><b>订阅导入</b></button>
		<button onclick="xuan(this,1)" value="replaceRule"><b>替换净化</b></button>
		<button onclick="xuan(this,1)" value="textTocRule"><b>TXT目录</b></button>
		<button onclick="xuan(this,1)" value="httpTTS"><b>TTS朗读</b></button>
		<button onclick="xuan(this,1)" value="theme"><b>阅读主题</b></button>
		<button onclick="xuan(this,1)" value="readConfig"><b>阅读排版</b></button>
</div>
<hr>
</div>

<br>
<div>
<p>导入阅读</p>
<textarea rows="6" placeholder="请输入资源链接" readonly></textarea>
<div>
		<button onclick="copy(2)"><b>复制链接</b></button>
		<button onclick="importu()" style="float:right"><b>导入阅读</b></button>
</div>
<hr>
</div>



<iframe hidden></iframe>
<script>


//选中标签
$=(rule)=>document.querySelectorAll(rule);


//获取textarea
textarea=(index)=>$('textarea')[index];


//删除active
omit=(A)=>Array.from(A,a=>a.className='');


//复制链接
function copy(index){
		textarea(index).select();//选中文本
		document.execCommand('copy');//复制
	}


//切换选择
xuan=(item,type)=>{
		type=type==0?'port':'path'
		btn=$(`.${type} button.active`)
		if(item.value==btn[0].value)return;
		omit(btn)
		item.className='active'
		geturl()
	}


//获取value
getu=(type,index)=>{
		u=String($('.'+type+' button.active')[0].value).split(',')[index].trim();

if(type=='path')u=`legado://import/${u}?src=`;
		return u
}


//获取url
geturl=()=>{
		url = String(textarea(0).value).trim()
if(/^\s*$/.test(url))return textarea(2).value='';

if(url.match(/lanzou/)){//蓝奏云url拼接
try{
		if(url.match(/#.+/)){
				pwd = url.split('#')[1]
				url = url.split('#')[0]
			}
		else{
				pwd = String(textarea(1).value).trim()
			}
