style:
/*移除链接下划线*/
a{
	text-decoration:none
}
body{
	width:98%;height:99%;
	position:absolute;margin:auto;
	top:0;left:0;right:0;bottom:0;
	display:flex;flex-direction:column;
}
body *{
	width:100%
}


.title {
	position:relative;
	border:1px solid#000;
	border-top-right-radius:6px;
	border-top-left-radius:6px;
	margin-bottom:25px
}
.title #tu {
	opacity:0.8;
	border-top-right-radius:5px;
	border-top-left-radius:5px;
}
.title .zuo {
	position:absolute;
	top:0;
	left:0;
	width:10%
}
.title .you {
	position:absolute;
	top:0;
	right:0;
	width:10%
}
.title .zuo img,.title .you img {
	border-radius:50%
}
.title .sou {
	position:absolute;
	bottom:0;
}
.title .sou img {
	width:30%
}
.title .sou form {
	width:70%;
	height:35px
}
.title .sou input {
	height:100%;
	float:left;
	outline:none;
	border:1px solid#000
}
.title .sou .input1 {
	width:80%;
	text-indent:10px;
	border-top-left-radius:10px;
	border-bottom-left-radius:10px
}
.title .sou .input2 {
	width:20%;
	border-left:0;
	color:000;
	border-top-right-radius:10px;
	border-bottom-right-radius:10px
}


.swiper-pagination{
	height:25px;
	border:1px solid#000;
	border-bottom-left-radius:6px;
	border-bottom-right-radius:6px;
	overflow-x:auto;overflow-y:hidden;
	display:flex;white-space:nowrap;
	position:absolute;top:100%;left:-1;
	background:#000;
}
.swiper-pagination{
	position:relative
}
.swiper-pagination-bullet-active{
	position:sticky;
	left:0;
	right:0
}
.swiper-slide{
	flex:auto;overflow-y:auto
}
.height{
	height:50%
}


.content{
	position:relative;
	margin:0;padding:0;
	display:flex;flex-wrap:wrap;
	border-bottom:1px solid#e2e2e2;
}
.content .zuo{
	width:70%;
}
.content .you{
	width:30%;
	display:block;
	text-align:center;
	position:relative;
}
.content .you button{
	width:auto;
	position:absolute;
	border:none;outline:none;
	border-radius:40px;left:30%;top:35%;
	background:-webkit-linear-gradient(left, #ff799e , #fe4d67);
	color:#fff;font-size:0.7rem;
	padding:0.25rem 0.55rem;
}
.content h5.xia{
	margin:0;padding:0;
}
.content b em {
	border:1px solid#ff4d65;
	color:#ff4d65;
	font-size:0.7rem;
	font-style:normal;
	border-radius:0.15rem;
	margin-right:0.25rem;
	padding:0rem 0.2rem
}
.content b em:nth-child(2) {
	border-color:#08acee;
	color:#08acee
}
.content b em:nth-child(3) {
	border-color:#f7912e;
	color:#f7912e
}

