		<button onclick="copy()"><b>复制链接</b></button>
		<button onclick="importu()" style="float:right"><b>导入阅读</b></button>
</div>



<iframe hidden></iframe>
<script>
//选中标签
$=(rule)=>document.querySelectorAll(rule);


//删除active
omit=(A)=>Array.from(A,a=>a.className='');


//复制链接
function copy(){
	$('textarea')[0].select()
	document.execCommand('copy')
	alert("复制成功")
}


//切换选择
xuan=(item)=>{
		btn=$(`.path button.active`);
		if(item.value==btn[0].value)return;
		omit(btn);
		return item.className='active';
	}


//获取url
importu=()=>{
		url = $('.path button.active')[0].value + $('textarea')[0].value;
		return $('iframe')[0].src = url;
	}


//初始化
(()=>{
		btnarr = [["资源下载",null],["自动识别","auto"],["书源导入","bookSource"],["订阅导入","rssSource"],["替换净化","replaceRule"],["TXT目录","textTocRule"],["TTS朗读","httpTTS"],["阅读主题","theme"],["阅读排版","readConfig"]].map($=>{
				$[1] = $[1]?`legado://import/${$[1]}?src=`:"";
				return `<button onclick="xuan(this)" value="${$[1]}"><b>${$[0]}</b></button>`;
			}).join('\n\t\t');
		$('.path')[0].innerHTML = '\n\t\t'+btnarr+'\n';
		return $('.path button')[0].className='active';
	})()
</script>

