<link rel="stylesheet" href="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.css">
<script src="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.js"></script>
<script>
//实现懒加载
lazy = new IntersectionObserver(imgs=>{
imgs.forEach(img=>{
		if(img.isIntersecting){
				img=img.target
				img.src=img.getAttribute('data-src')
				lazy.unobserve(img)
			}
	})
})

//遍历观察所有img
Array.from(document.querySelectorAll('img'),img=>lazy.observe(img))

//支持查看大图
new Viewer(document.getElementById('box'),{title:false,interval:3000})
</script>

