var chapts=java.ajaxAll(result.split('\n').map(c=>"https://www.yeseimg.com"+c));
var imgs=[];
for(var i=0;i<chapts.length;i++){
      var html=chapts[i].body();
      html=html.replaceAll('\n','').replaceAll('\t','').replaceAll('\r','').replaceAll(' ','');
      html=html.substr(html.indexOf('chapter_footer_toggle()">')+25);
      html=html.substring(0,html.indexOf('</article>')-9);
      var igs=html.split('figure><figureclass="item">');
      for(var j=0;j<igs.length;j++){
            var ig=igs[j];
             ig=ig.substr(ig.indexOf('data-original="')+15);
             ig=ig.substring(0,ig.indexOf('"style="'))
             imgs.push(ig)
      }
}


var h=imgs.map(d => "<img class='lazy' data-origanl='" + d + "'/>").join('');


