@js:
<!---获取gitee作者无MD资源列表方法---->
function ui(){
var list =
java.getElements("class.five wide column tree-item-file-name tree-list-item");
var doc =org.jsoup.Jsoup.parse(list)
var elements = doc.select("a");
var all = "";
for(var i = 0;i<elements.size();i++){
all += "<h5>"+elements.get(i).attr("title")+"</h5><a>https://www.gitee.com"+elements.get(i).attr("href")+"</a><hr>"+"\n"
}
var str = String(all).replace(/blob/g,"raw")
return str;
}

<!------gitee无MD作者生成页面-------->
<!------在这里按例子添加用户名-------->

if(baseUrl.match(/(baize)|(zmn)|(onede)|(jia_to_hui)|(pxysen)/)){
result = ui()}
else{
<!------其他有MD作者生成页面-------->

result =java.getElements("class.markdown-body entry-content container-lg&&class.file_content markdown-body&&class.duta").html()
}

