if(html.match(/"size":\s*"large",\s*"url":\s*"[^"]+"/g)){
a=html.match(/"size":\s*"[^"]+",\s*"url":\s*"[^"]+"/g);
    var cimg=[];
    for (i=0;i<a.length;i++){
       cimg.push(a[i].match(/url":\s*"([^"]+)"/)[1]);
    };
    var bimg="";
    for (i=0;i<cimg.length;i++){
        bimg=bimg+"<p></p><img src="+cimg[i]+"><img>"
    }
result=result+bimg; 
}else{
    if(html.match(/"stream_url":\s*"[^"]+"/)){
 if(html.match(/"mp4_720p_mp4":\s*"[^"]+"/)){a=html.match(/"mp4_720p_mp4":\s*"([^"]+)"/)[1];}else{
    if(html.match(/"mp4_hd_mp4":\s*"[^"]+"/)){a=html.match(/"mp4_hd_mp4":\s*"([^"]+)"/)[1];}else{
a=html.match(/"mp4_ld_mp4":\s*"([^"]+)"/)[1];
}
}

result=result.replace(/data-url="([^"]+)"\s*href="([^"]+)"/,"data-url=\"$1\" href=\""+a+"\"");
dimg=html.match(/"page_pic":[\s\S]+?"url":\s*"([^"]+)"./)[1];
result=result+"<video src=\""+a+"\" width=\"350px\" height=\"250px\" controls=\"controls\"  poster=\""+dimg+"\"></video>";
}    else{result=result
}};
if(JSON.parse(java.ajax("https://m.weibo.cn/comments/hotflow?id="+id+"&mid="+id+"&max_id_type=0")).ok==1){
list=JSON.parse(java.ajax("https://m.weibo.cn/comments/hotflow?id="+id+"&mid="+id+"&max_id_type=0")).data.data;
result="<a href="+b+">原文地址</a>"+"<p></p>"+result;
    for(i=0;i<list.length;i++){
    details="";
    if(list[i].pic!=undefined){
    pic=list[i].pic.large.url;
    details="<details><summary>查看图片</summary><p><img src=\""+pic+"\" alt=\"查看图片\"></p></details>"
}
