var content = java.getElement(".article-content")

var doc = org.jsoup.Jsoup.parse(content);

//解析剩下页数,并追加剩余页数内容

var pageNum = null;
var other = [];
if("{{@@class.article-paging}}"!=null){
var page = doc.select(".article-paging").select("a");
pageNum = page.size();
for(var i = 0;i < pageNum;i++){
var doc = org.jsoup.Jsoup.connect(page.get(i).attr("href")).get()
other[i] = doc.select(".article-content").html();
}

//拆分文章元信息，以便自定义样式

var list = info.split(" ");
}

result = title+'<a id = "meta">'+list[0]+' '+list[1]+' 浏览: '+list[3]+'</a><hr>'+content+other.join(" ")+'<hr><p><a href='+baseUrl+'>原文出处</a>'

