rulePubDate:
@js:

/*自定义日期方法：字符串yyyy/mm/dd转为xxxx年xx月xx日(星期x)*/

function myFormat(str) {
var date = new Date(str);
var weeks = new Array("星期日","星期一","星期二","星期三","星期四","星期五","星期六");
var week = weeks[date.getDay()];
var time = date.getFullYear()+"年"+(date.getMonth()+1)+"月"+date.getDate()+"日"+" ("+week+")"
return "更新日期: "+time;
}

//github获取时间方法(md文件含描述)

if(baseUrl.match(/XIU2/)){
var str = "{{@@class.css-truncate css-truncate-target d-block width-fit.15@text}}";
result = myFormat(str);
}

//gitee获取时间方法(md文件含描述)

else if(baseUrl.match(/(vpq)|(namofree)|(slaijie)|(bushixuanqi)/)){
var time = "{{@@class.contributor-description@text}}";
var str = time.replace(/\-/g,"\/").match(/2.{9}/);
result = myFormat(str);
}

//gitee获取时间方法(md文件不含描述)

else if(baseUrl.match(/(baize)|(zmn)|(onede)|(jia_to_hui)|(pxysen)/)){
var list = java.getElements(".timeago");
var doc =org.jsoup.Jsoup.parse(list)
var time = doc.select("span").attr("datetime");
var str = time.match(/2.{9}/);
result = myFormat(str);
}

//其它获取时间方法

else if(baseUrl.match(/SanQianShuYuan/)){
var time = "{{@@class.post-ctx@text}}"
var year = time.match(/2[\d]{3}/);
var month = time.match(/年\s[\d]{2}/);
var day = time.match(/月\s[\d]{2}/);
var str = (year+month+day).replace(/(年\s)|(月\s)/g,"\/");
result = myFormat(str);
}

